CREATE TABLE `esut_gamma`.`lecturers` ( `id` INT NOT NULL AUTO_INCREMENT , `firstname` VARCHAR(50) NOT NULL , `lastname` VARCHAR(50) NOT NULL , `email` VARCHAR(200) NOT NULL , `phone` VARCHAR(50) NOT NULL , `department` INT NOT NULL , `courses_handling` JSON NOT NULL , `password` TEXT NOT NULL , `title` VARCHAR(200) NULL , `date_joined` DATE NOT NULL DEFAULT CURRENT_TIMESTAMP , `date_updated` DATETIME on update CURRENT_TIMESTAMP NOT NULL DEFAULT CURRENT_TIMESTAMP , PRIMARY KEY (`id`), UNIQUE `email` (`email`), UNIQUE `phone` (`phone`)) ENGINE = InnoDB;